package com.geeks.tilek_talaibekov_3_4;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.geeks.tilek_talaibekov_3_4.databinding.FragmentFirstBinding;
import com.geeks.tilek_talaibekov_3_4.databinding.FragmentSecondBinding;

import java.util.ArrayList;

public class SecondFragment extends Fragment {

    private FragmentSecondBinding binding;
    private int position;
    private ArrayList<CountryModel> countryList = new ArrayList<>();


    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentSecondBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        position = getArguments().getInt("key");
        checkPosition(position);
        CountryAdapter adapter = new CountryAdapter(countryList);
        binding.rvCountries.setAdapter(adapter);
    }

    private void checkPosition(int position) {
        if (position == 0){
            loadEurasia();
        }
    }

    private void loadEurasia() {
        countryList.add(new CountryModel("data:image/png;base64,", "Russia"));
        countryList.add(new CountryModel("data:image/png;base64,", "Germany"));
        countryList.add(new CountryModel("data:image/png;base64,", "France"));
        countryList.add(new CountryModel("data:image/png;base64,", "Poland"));
        countryList.add(new CountryModel("data:image/png;base64,", "Ukraine"));
    }


}